import React,{ useRef } from "react";
import styled from "styled-components";

import { Text, Grid} from ".";

const Input = (props)=> {
    const Ref = useRef();
    const {label, placeholder, _onChange} =props;

    //value에 따라 값 저장시 분류

    return(
        <React.Fragment>
            <Text margin="0">{label}</Text>
            <input ref={Ref} placeholder={placeholder} onChange={_onChange}/>
        </React.Fragment>
    )
}


Input.defaultProps = {
    label : "텍스트",
    placeholder: "입력해주세요",
    _onChange: () => {}
}

const tagInput = styled.input`
    border: 1px solid #212121;
    width: 100%;
    padding: 12px 4px;
    box-sizing: border-box;
`

export default Input;
